insert  into `ldwj_monitordetail`(`ID`,`MONITORID`,`CREATETIME`,`REMARK`,`DELSTATUS`,`CODE`,`OVERTIME`,`CYCLEPERIOD`,`INPUT`,`TCPPORTS`,`ALARMLEVEL`) values (1,1,NULL,'ARP监视器利用ARP Request/Response消息来确定被测设备网络协议栈是否可用。每个ARP Request大约每秒发送一次，通过获取回应报文来绘制监控图。如果长期没有ARP Response，那么就发送告警日志。默认超时时间：1000ms',0,NULL,1000,0,0,2,0),(2,2,NULL,'ICMP监视器利用ICMP Echo Request/Response消息去确定被测设备的网络协议栈是否可用。按照周期发送ICMP Echo Request，并获得ICMP Echo Response的时间从而决定ICMP监视器的状态。ICMP的超时时间是可配置的。默认超时时间：1000ms',0,NULL,1000,0,0,2,0),(3,3,NULL,'TCP端口监视器通过获得指定的TCP端口来确定被测设备端口是开启还是关闭。监视器用TCP连接每个开启的端口，但不发送任何数据就关闭连接。监视器每秒确定一次这些端口的状态，如果任何一个端口在连接过程中被关闭就向事件日志发告警。',0,NULL,0,0,0,1,0),(4,4,NULL,'离散数据监视器从被测设备上按照周期来获取数字输出，从而判断被测设备的逻辑是否还处于工作状态。',0,NULL,0,1000,1,2,10);

