/*Table structure for table `security_check_baseline` */

DROP TABLE IF EXISTS `security_check_baseline`;

CREATE TABLE `security_check_baseline` (
  `ID` varchar(32) COLLATE utf8_unicode_ci NOT NULL COMMENT '主键ID',
  `BL_NAME` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '选项名称',
  `VALS` varchar(11) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '值',
  `EXPLAINS` varchar(500) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '说明描述',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='安全检查基线';

/*Data for the table `security_check_baseline` */

/*Table structure for table `security_check_rb` */

DROP TABLE IF EXISTS `security_check_rb`;

CREATE TABLE `security_check_rb` (
  `ID` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '主键ID',
  `BASELINE_ID` varchar(32) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '基线ID',
  `RESULT_ID` varchar(32) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '结果ID',
  `VAL` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '实际结果值',
  `BASELINE_NAME` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '基线名称',
  `BASELINE_VAL` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '基线值',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='检查结果和基线关联的表';

/*Data for the table `security_check_rb` */

/*Table structure for table `security_check_result` */

DROP TABLE IF EXISTS `security_check_result`;

CREATE TABLE `security_check_result` (
  `ID` varchar(32) COLLATE utf8_unicode_ci NOT NULL,
  `DEVICE_NAME` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '设备名称',
  `EQUIPMENT_ID` int(11) DEFAULT NULL COMMENT '设备ID',
  `CREATE_TIME` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '创建时间',
  `FULL_NAME` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '全称（设备名称+时间）',
  `DEL_STATUS` tinyint(4) unsigned zerofill DEFAULT NULL COMMENT '删除状态默认0表示未删除',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='安全检查结果';

/*Data for the table `security_check_result` */

/*Table structure for table `sys_datadict` */

DROP TABLE IF EXISTS `sys_datadict`;

CREATE TABLE `sys_datadict` (
  `ID` int(11) NOT NULL AUTO_INCREMENT,
  `DATA_CODE` int(11) DEFAULT NULL COMMENT '数据编码',
  `DATA_NAME` int(2) DEFAULT NULL COMMENT '名称',
  `REMARK` varchar(256) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '备注',
  `DELTATUS` int(2) DEFAULT '0' COMMENT '0：未删除\r\n            1：已删除',
  `CREATETIME` datetime DEFAULT NULL COMMENT '创建时间',
  `CODE` varchar(10) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '编码',
  `UP_DATA_ID` int(11) DEFAULT NULL COMMENT '父级ID',
  `UP_DATA_NAME` varchar(64) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '父极名称',
  `REMARK1` int(11) DEFAULT NULL,
  `DATA_TYPE` int(11) DEFAULT NULL COMMENT '名称对应编码',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='字典表';

/*Data for the table `sys_datadict` */

